import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';
import TrackList from './TrackList';
import './App.css';
import Header from '../../settings/AccentColorContext';

import AccentColorContext from '../pages/settings/AccentColorContext';

const App = () => {
    const [tracks, setTracks] = useState([]);
    const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
    const { accentColor } = useContext(AccentColorContext);

    const handleSearch = async (query) => {
        try {
            const response = await axios.get(`https://www.googleapis.com/youtube/v3/search`, {
                params: {
                    part: 'snippet',
                    q: query,
                    type: 'video',
                    videoCategoryId: '10', // Category ID for Music
                    key: 'AIzaSyCNaJfGAy9a8LlB8B_A3jSRlNxpq4jsCAI'
                }
            });
            setTracks(response.data.items);
            setCurrentTrackIndex(null);
        } catch (error) {
            console.error('Error fetching data from YouTube API', error);
        }
    };

    const handlePlay = (index) => {
        setCurrentTrackIndex(index);
    };

    const getCurrentVideoId = () => {
        if (currentTrackIndex !== null && tracks.length > 0) {
            return tracks[currentTrackIndex].id.videoId;
        }
        return null;
    };

    return (
        <div className="App" style={{ borderColor: accentColor }}>
            <Header videoId={getCurrentVideoId()} />
            <h1>Music Player</h1>
            <SearchBar onSearch={handleSearch} />
            <TrackList tracks={tracks} onPlay={handlePlay} />
        </div>
    );
};

export default App;
