import React, { useEffect, useState, useContext, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styles from './Header.module.css';
import AccentColorContext from '../pages/settings/AccentColorContext';
import YouTube from 'react-youtube';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause, faBackward, faForward } from '@fortawesome/free-solid-svg-icons';

const Header = ({ videoId, trackTitle, trackArtist }) => {
    const [time, setTime] = useState(new Date());
    const location = useLocation();
    const { accentColor } = useContext(AccentColorContext);
    const playerRef = useRef(null);

    const onReady = (event) => {
        playerRef.current = event.target;
    };

    const playVideo = () => {
        if (playerRef.current) playerRef.current.playVideo();
    };

    const pauseVideo = () => {
        if (playerRef.current) playerRef.current.pauseVideo();
    };

    const nextVideo = () => {
        if (playerRef.current) playerRef.current.nextVideo();
    };

    const prevVideo = () => {
        if (playerRef.current) playerRef.current.previousVideo();
    };

    const opts = {
        height: '50',
        width: '80',
        playerVars: {
            autoplay: 1,
            controls: 0,
        },
    };

    useEffect(() => {
        const timer = setInterval(() => setTime(new Date()), 1000);
        return () => clearInterval(timer);
    }, []);

    return (
        <div className={styles.header} style={{ borderColor: accentColor }}>
            <div className={styles.logo}>
                <div className="logo">
                    <span className={styles.networkName} style={{ color: accentColor }}>S</span>
                    <img src="logo-fenix.png" alt="Logo" className={styles.logoImage} />
                    <span className={styles.networkName} style={{ color: accentColor }}>N</span>
                </div>
            </div>
            <div className={styles.divider} style={{ backgroundColor: accentColor }}></div>
            {videoId ? (
                <div className={styles['music-player-header']}>
                    <YouTube videoId={videoId} opts={opts} onReady={onReady} />
                    <div className={styles.trackInfo}>
                        <div className={styles.trackTitle}>{trackTitle}</div>
                        <div className={styles.trackArtist}>{trackArtist}</div>
                    </div>
                    <div className={styles.controls}>
                        <button onClick={prevVideo}><FontAwesomeIcon icon={faBackward} /></button>
                        <button onClick={playVideo}><FontAwesomeIcon icon={faPlay} /></button>
                        <button onClick={pauseVideo}><FontAwesomeIcon icon={faPause} /></button>
                        <button onClick={nextVideo}><FontAwesomeIcon icon={faForward} /></button>
                    </div>
                </div>
            ) : (
                <div className={styles.clock}>
                    {time.toLocaleTimeString()}
                </div>
            )}
        </div>
    );
};

export default Header;
