import React, { useEffect, useState, useContext, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import styles from './Header.module.css';
import AccentColorContext from '../pages/settings/AccentColorContext';
import YouTube from 'react-youtube';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause, faBackward, faForward, faVolumeUp, faExpand, faCompress } from '@fortawesome/free-solid-svg-icons';

const Header = ({ videoId, trackTitle, trackArtist }) => {
    const [time, setTime] = useState(new Date());
    const location = useLocation();
    const { accentColor } = useContext(AccentColorContext);
    const playerRef = useRef(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [isExpanded, setIsExpanded] = useState(false);
    const [isWidgetExpanded, setIsWidgetExpanded] = useState(false);
    const [volume, setVolume] = useState(50);
    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const [trackHistory, setTrackHistory] = useState([]);

    const onReady = (event) => {
        playerRef.current = event.target;
        playerRef.current.setVolume(volume);
    };

    const playVideo = () => {
        if (playerRef.current) {
            playerRef.current.playVideo();
            setIsPlaying(true);
        }
    };

    const pauseVideo = () => {
        if (playerRef.current) {
            playerRef.current.pauseVideo();
            setIsPlaying(false);
        }
    };

    const nextVideo = () => {
        // Реализация логики переключения к следующему видео
        if (playerRef.current) {
            const nextTrackIndex = currentTrackIndex + 1;
            setCurrentTrackIndex(nextTrackIndex);
            setTrackHistory([...trackHistory, nextTrackIndex]);
            playerRef.current.nextVideo();
        }
    };

    const prevVideo = () => {
        if (trackHistory.length > 0) {
            const prevTrackIndex = trackHistory.pop();
            setCurrentTrackIndex(prevTrackIndex);
            playerRef.current.loadVideoById({ videoId: trackHistory[prevTrackIndex] });
            setIsPlaying(true);
        }
    };

    const togglePlayPause = () => {
        if (isPlaying) {
            pauseVideo();
        } else {
            playVideo();
        }
    };

    const toggleExpand = () => {
        setIsExpanded(!isExpanded);
    };

    const toggleWidgetExpand = () => {
        setIsWidgetExpanded(!isWidgetExpanded);
    };

    const handleVolumeChange = (event) => {
        const newVolume = event.target.value;
        setVolume(newVolume);
        if (playerRef.current) {
            playerRef.current.setVolume(newVolume);
        }
    };

    const opts = {
        height: isExpanded ? '200' : '50',
        width: isExpanded ? '300' : '80',
        playerVars: {
            autoplay: 1,
            controls: 0,
        },
    };

    useEffect(() => {
        const timer = setInterval(() => setTime(new Date()), 1000);
        return () => clearInterval(timer);
    }, []);

    return (
        <div className={styles.header} style={{ borderColor: accentColor }}>
            <div className={styles.logo}>
                <div className="logo">
                    <span className={styles.networkName} style={{ color: accentColor }}>S</span>
                    <img src="logo-fenix.png" alt="Logo" className={styles.logoImage} />
                    <span className={styles.networkName} style={{ color: accentColor }}>N</span>
                </div>
            </div>
            <div className={styles.divider} style={{ backgroundColor: accentColor }}></div>
            {videoId ? (
                <div className={`${styles['music-player-header']} ${isWidgetExpanded ? styles.expanded : ''}`} onClick={toggleWidgetExpand}>
                    <div className={styles.playerContainer}>
                        <YouTube videoId={videoId} opts={opts} onReady={onReady} />
                        <div className={styles.trackInfo}>
                            <div className={styles.trackTitle} style={{ color: accentColor }}>{trackTitle}</div>
                            <div className={styles.trackArtist}>{trackArtist}</div>
                        </div>
                    </div>
                    <div className={styles.controls}>
                        <button onClick={prevVideo} disabled={trackHistory.length === 0}>
                            <FontAwesomeIcon icon={faBackward} />
                        </button>
                        <button onClick={togglePlayPause}>
                            <FontAwesomeIcon icon={isPlaying ? faPause : faPlay} />
                        </button>
                        <button onClick={nextVideo}>
                            <FontAwesomeIcon icon={faForward} />
                        </button>
                        <button onClick={toggleExpand}>
                            <FontAwesomeIcon icon={isExpanded ? faCompress : faExpand} />
                        </button>
                    </div>
                    {isWidgetExpanded && (
                        <div className={styles.expandedControls}>
                            <div className={styles.timeInfo}>
                                <span>0:00</span>
                                <input
                                    type="range"
                                    min="0"
                                    max="100"
                                    value="0"
                                    className={styles.seekBar}
                                    style={{ accentColor: accentColor }}
                                />
                                <span>4:00</span>
                            </div>
                            <div className={styles.volumeControl}>
                                <FontAwesomeIcon icon={faVolumeUp} />
                                <input
                                    type="range"
                                    min="0"
                                    max="100"
                                    value={volume}
                                    onChange={handleVolumeChange}
                                    className={styles.volumeBar}
                                    style={{ accentColor: accentColor }}
                                />
                            </div>
                        </div>
                    )}
                </div>
            ) : (
                <div className={styles.clock}>
                    {time.toLocaleTimeString()}
                </div>
            )}
        </div>
    );
};

export default Header;
