import React, { useEffect, useState, useContext, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import styles from './Header.module.css';
import AccentColorContext from '../pages/settings/AccentColorContext';
import YouTube from 'react-youtube';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause, faBackward, faForward, faVolumeUp, faExpand, faCompress, faSpinner } from '@fortawesome/free-solid-svg-icons';

const Header = ({ videoId, trackTitle, trackArtist }) => {
    const [time, setTime] = useState(new Date());
    const location = useLocation();
    const { accentColor } = useContext(AccentColorContext);
    const playerRef = useRef(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [isExpanded, setIsExpanded] = useState(false);
    const [isWidgetExpanded, setIsWidgetExpanded] = useState(false);
    const [volume, setVolume] = useState(50);
    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const [trackHistory, setTrackHistory] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    const onReady = (event) => {
        playerRef.current = event.target;
        playerRef.current.setVolume(volume);
        setIsLoading(false);
    };

    const onStateChange = (event) => {
        const { data } = event;
        switch (data) {
            case 1: // Playing
                setIsPlaying(true);
                setIsLoading(false);
                break;
            case 2: // Paused
                setIsPlaying(false);
                setIsLoading(false);
                break;
            case 3: // Buffering
                setIsLoading(true);
                break;
            case 0: // Ended
                nextVideo();
                break;
            default:
                break;
        }
    };

    const playVideo = () => {
        if (playerRef.current) {
            playerRef.current.playVideo();
        }
    };

    const pauseVideo = () => {
        if (playerRef.current) {
            playerRef.current.pauseVideo();
        }
    };

    const nextVideo = (event) => {
        if (event) event.stopPropagation();
        if (playerRef.current) {
            const nextTrackIndex = currentTrackIndex + 1;
            setCurrentTrackIndex(nextTrackIndex);
            setTrackHistory([...trackHistory, nextTrackIndex]);
            setIsLoading(true);
            playerRef.current.nextVideo();
        }
    };

    const prevVideo = (event) => {
        if (event) event.stopPropagation();
        if (trackHistory.length > 0) {
            const prevTrackIndex = trackHistory.pop();
            setCurrentTrackIndex(prevTrackIndex);
            setTrackHistory(trackHistory);
            setIsLoading(true);
            playerRef.current.loadVideoById({ videoId: trackHistory[prevTrackIndex] });
        }
    };

    const togglePlayPause = (event) => {
        event.stopPropagation();
        if (isPlaying) {
            pauseVideo();
        } else {
            playVideo();
        }
    };

    const toggleExpand = (event) => {
        event.stopPropagation();
        setIsExpanded(!isExpanded);
    };

    const toggleWidgetExpand = (event) => {
        event.stopPropagation();
        setIsWidgetExpanded(!isWidgetExpanded);
        if (isExpanded) {
            setIsExpanded(false);
        }
    };

    const handleVolumeChange = (event) => {
        const newVolume = event.target.value;
        setVolume(newVolume);
        if (playerRef.current) {
            playerRef.current.setVolume(newVolume);
        }
    };

    const opts = {
        height: isExpanded ? '150' : '50',
        width: isExpanded ? '250' : '80',
        playerVars: {
            autoplay: 1,
            controls: 0,
        },
    };

    useEffect(() => {
        const timer = setInterval(() => setTime(new Date()), 1000);
        return () => clearInterval(timer);
    }, []);

    useEffect(() => {
        const handleBeforeUnload = () => localStorage.setItem('trackHistory', JSON.stringify(trackHistory));
        window.addEventListener('beforeunload', handleBeforeUnload);
        return () => window.removeEventListener('beforeunload', handleBeforeUnload);
    }, [trackHistory]);

    useEffect(() => {
        const savedTrackHistory = localStorage.getItem('trackHistory');
        if (savedTrackHistory) {
            setTrackHistory(JSON.parse(savedTrackHistory));
        }
    }, []);

    return (
        <div className={styles.header} style={{ borderColor: accentColor }}>
            <div className={styles.logo}>
                <div className="logo">
                    <span className={styles.networkName} style={{ color: accentColor }}>S</span>
                    <img src="logo-fenix.png" alt="Logo" className={styles.logoImage} />
                    <span className={styles.networkName} style={{ color: accentColor }}>N</span>
                </div>
            </div>
            <div className={styles.divider} style={{ backgroundColor: accentColor }}></div>
            <div className={`${styles['music-player-header']}`}>
                {time.toLocaleTimeString()}
            </div>
            {videoId && (
                <div className={`${styles['music-player-header']} ${isWidgetExpanded ? styles.expanded : ''}`} onClick={toggleWidgetExpand}>
                    <div className={styles.playerContainer}>
                        <YouTube videoId={videoId} opts={opts} onReady={onReady} onStateChange={onStateChange} />
                        {!isExpanded && (
                            <div className={styles.playerBlock}>
                                <div className={styles.trackInfo}>
                                    <div className={styles.trackTitle} style={{ color: accentColor }}>{trackTitle}</div>
                                    <div className={styles.trackArtist}>{trackArtist}</div>
                                </div>
                                <div className={styles.controls}>
                                    <button onClick={prevVideo} disabled={trackHistory.length === 0 || isLoading}>
                                        <FontAwesomeIcon icon={faBackward} style={{ color: accentColor }} />
                                    </button>
                                    <button onClick={togglePlayPause}>
                                        {isLoading ? (
                                            <FontAwesomeIcon icon={faSpinner} spin style={{ color: accentColor }} />
                                        ) : (
                                            <FontAwesomeIcon icon={isPlaying ? faPause : faPlay} style={{ color: accentColor }} />
                                        )}
                                    </button>
                                    <button onClick={nextVideo} disabled={isLoading}>
                                        <FontAwesomeIcon icon={faForward} style={{ color: accentColor }} />
                                    </button>
                                    <button onClick={toggleExpand}>
                                        <FontAwesomeIcon icon={isExpanded ? faCompress : faExpand} style={{ color: accentColor }} />
                                    </button>
                                </div>
                            </div>
                        )}
                    </div>
                    {isWidgetExpanded && (
                        <div className={styles.expandedControls}>
                            <div className={styles.timeInfo}>
                                <span>0:00</span>
                                <input
                                    type="range"
                                    min="0"
                                    max="100"
                                    value="0"
                                    className={styles.seekBar}
                                    style={{ accentColor: accentColor }}
                                />
                                <span>4:00</span>
                            </div>
                            <div className={styles.volumeControl}>
                                <FontAwesomeIcon icon={faVolumeUp} />
                                <input
                                    type="range"
                                    min="0"
                                    max="100"
                                    value={volume}
                                    onChange={handleVolumeChange}
                                    className={styles.volumeBar}
                                    style={{ accentColor: accentColor }}
                                />
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Header;
