import React, { createContext, useState, useEffect } from 'react';

const AccentColorContext = createContext();
const BackgroundContext = createContext();

export const AccentColorProvider = ({ children }) => {
    const defaultColor = '#000000'; // Дефолтный цвет акцента
    const savedColor = localStorage.getItem('settings_accent_color') || defaultColor;
    const [accentColor, setAccentColor] = useState(savedColor);

    useEffect(() => {
        const savedColor = localStorage.getItem('settings_accent_color');
        if (savedColor) {
            setAccentColor(savedColor);
        }
    }, []);

    return (
        <AccentColorContext.Provider value={{ accentColor, setAccentColor }}>
            {children}
        </AccentColorContext.Provider>
    );
};

export const BackgroundProvider = ({ children }) => {
    const defaultBackground = {
        type: 'color',
        colors: ['#000000'],
        image: '',
        opacity: 1
    };
    const savedBackground = JSON.parse(localStorage.getItem('settings_background')) || defaultBackground;
    const [backgroundSettings, setBackgroundSettings] = useState(savedBackground);

    useEffect(() => {
        const savedBackground = JSON.parse(localStorage.getItem('settings_background')) || defaultBackground;
        setBackgroundSettings(savedBackground);
    }, []);

    const updateBackground = (newSettings) => {
        setBackgroundSettings(newSettings);
        localStorage.setItem('settings_background', JSON.stringify(newSettings));
    };

    return (
        <BackgroundContext.Provider value={{ backgroundSettings, updateBackground }}>
            {children}
        </BackgroundContext.Provider>
    );
};

export { AccentColorContext, BackgroundContext };
