import React, { useState, useEffect, useContext } from 'react';
import AccentColorContext from './AccentColorContext';
import styles from './GeneralSettings.module.css';

const GeneralSettings = () => {
    const { accentColor, setAccentColor, backgroundSettings, setBackgroundSettings } = useContext(AccentColorContext);
    const [tempColor, setTempColor] = useState(accentColor);
    const [backgroundType, setBackgroundType] = useState(backgroundSettings.type);
    const [backgroundColor, setBackgroundColor] = useState(backgroundSettings.color);
    const [backgroundImage, setBackgroundImage] = useState(backgroundSettings.image);
    const [backgroundDarkness, setBackgroundDarkness] = useState(backgroundSettings.darkness);
    const [gradientColors, setGradientColors] = useState(backgroundSettings.gradientColors);

    const [isColorChanged, setIsColorChanged] = useState(false);
    const [unsavedChanges, setUnsavedChanges] = useState(false);

    useEffect(() => {
        setTempColor(accentColor);
    }, [accentColor]);

    const handleColorChange = (event) => {
        setTempColor(event.target.value);
        setIsColorChanged(true);
        setUnsavedChanges(true);
        setAccentColor(event.target.value);
    };

    const handleBackgroundTypeChange = (event) => {
        setBackgroundType(event.target.value);
        setUnsavedChanges(true);
    };

    const handleRevert = () => {
        const savedColor = localStorage.getItem('settings_accent_color');
        if (savedColor) {
            setTempColor(savedColor);
            setIsColorChanged(false);
            setAccentColor(savedColor);
        }
    };

    const handleBackgroundColorChange = (event) => {
        setBackgroundColor(event.target.value);
        setUnsavedChanges(true);
    };

    const handleBackgroundImageChange = (event) => {
        setBackgroundImage(event.target.value);
        setUnsavedChanges(true);
    };

    const handleBackgroundDarknessChange = (event) => {
        setBackgroundDarkness(event.target.value);
        setUnsavedChanges(true);
    };

    const handleAddGradientColor = () => {
        if (gradientColors.length < 6) {
            setGradientColors([...gradientColors, { color: '#ffffff', type: gradientColors[0].type }]);
            setUnsavedChanges(true);
        }
    };

    const handleGradientColorChange = (index, event) => {
        const newGradientColors = [...gradientColors];
        newGradientColors[index].color = event.target.value;
        setGradientColors(newGradientColors);
        setUnsavedChanges(true);
    };

    const handleGradientTypeChange = (event) => {
        const newGradientColors = gradientColors.map(gradient => ({
            ...gradient,
            type: event.target.value,
        }));
        setGradientColors(newGradientColors);
        setUnsavedChanges(true);
    };

    const generateBackgroundCSS = () => {
        if (backgroundType === 'color') {
            if (gradientColors.length > 1) {
                const gradientString = gradientColors.map(colorObj => colorObj.color).join(', ');
                return `${gradientColors[0].type}-gradient(${gradientString})`;
            }
            return backgroundColor;
        }
        return `url(${backgroundImage})`;
    };

    const handleSave = () => {
        const background = {
            type: backgroundType,
            color: backgroundColor,
            image: backgroundImage,
            darkness: backgroundDarkness,
            gradientColors,
        };

        localStorage.setItem('settings_accent_color', tempColor);
        localStorage.setItem('settings_background', JSON.stringify(background));

        setBackgroundSettings({
            ...background,
            css: generateBackgroundCSS(),
        });
        setIsColorChanged(false);
        setUnsavedChanges(false);
    };

    const handleCancel = () => {
        const savedColor = localStorage.getItem('settings_accent_color');
        const savedBackground = JSON.parse(localStorage.getItem('settings_background'));

        if (savedColor) {
            setTempColor(savedColor);
            setAccentColor(savedColor);
        }

        if (savedBackground) {
            setBackgroundType(savedBackground.type);
            setBackgroundColor(savedBackground.color);
            setBackgroundImage(savedBackground.image);
            setBackgroundDarkness(savedBackground.darkness);
            setGradientColors(savedBackground.gradientColors);
        }

        setIsColorChanged(false);
        setUnsavedChanges(false);
    };

    return (
        <div className={styles.settingsContainer}>
            <h2>Общие и Персонализация</h2>
            {unsavedChanges && (
                <div className={styles.unsavedChanges}>
                    Есть несохраненные изменения
                </div>
            )}
            <h3>Основные</h3>
            <div className={styles.settingItem}>
                <label>Язык аккаунта:</label>
                <select defaultValue="ru" disabled>
                    <option value="ru">Русский</option>
                    <option value="en" disabled>English</option>
                </select>
            </div>
            <div className={styles.settingItem}>
                <label>Тема:</label>
                <select defaultValue="dark" disabled>
                    <option value="dark">Темная</option>
                    <option value="light" disabled>Светлая</option>
                </select>
            </div>
            <h3>Цвет и персонализация</h3>
            <div className={styles.settingItem}>
                <label>Цвет акцента:</label>
                <input
                    type="color"
                    value={tempColor}
                    onChange={handleColorChange}
                    className={styles.colorPicker}
                />
            </div>
            {isColorChanged && (
                <div className={styles.revertContainer}>
                    <span>Отменить сохранение цвета на </span>
                    <div
                        className={styles.revertColorBox}
                        style={{ backgroundColor: localStorage.getItem('settings_accent_color') }}
                        onClick={handleRevert}
                    />
                </div>
            )}
            <div className={styles.settingItem}>
                <label>Тип фона:</label>
                <select value={backgroundType} onChange={handleBackgroundTypeChange}>
                    <option value="color">Цвет</option>
                    <option value="image">Изображение</option>
                </select>
            </div>
            {backgroundType === 'color' ? (
                <div className={styles.settingItem}>
                    <label>Цвет фона:</label>
                    <input
                        type="color"
                        value={backgroundColor}
                        onChange={handleBackgroundColorChange}
                        className={styles.colorPicker}
                    />
                    <button onClick={handleAddGradientColor}>Добавить цвет</button>
                    <select
                        value={gradientColors[0]?.type || 'linear'}
                        onChange={handleGradientTypeChange}
                    >
                        <option value="linear">Линейный</option>
                        <option value="radial">Радиальный</option>
                    </select>
                    {gradientColors.map((gradient, index) => (
                        <div key={index} className={styles.gradientColor}>
                            <input
                                type="color"
                                value={gradient.color}
                                onChange={(event) => handleGradientColorChange(index, event)}
                                className={styles.colorPicker}
                            />
                        </div>
                    ))}
                </div>
            ) : (
                <div className={styles.settingItem}>
                    <label>Ссылка на изображение:</label>
                    <input
                        type="text"
                        value={backgroundImage}
                        onChange={handleBackgroundImageChange}
                    />
                    <div className={styles.settingItem}>
                        <label>Затемнение изображения:</label>
                        <input
                            type="range"
                            min="0.1"
                            max="1"
                            step="0.1"
                            value={backgroundDarkness}
                            onChange={handleBackgroundDarknessChange}
                        />
                    </div>
                </div>
            )}
            {unsavedChanges && (
                <div className={styles.buttonsContainer}>
                    <button
                        onClick={handleSave}
                        className={styles.saveButton}
                    >
                        Сохранить настройки
                    </button>
                    <button
                        onClick={handleCancel}
                        className={styles.cancelButton}
                    >
                        Отменить изменения
                    </button>
                </div>
            )}
        </div>
    );
};

export default GeneralSettings;
