import React, { createContext, useState, useEffect } from 'react';

const AccentColorContext = createContext();

export const AccentColorProvider = ({ children }) => {
    const defaultColor = 'violet'; // Дефолтный цвет, который сейчас в стилях
    const savedColor = localStorage.getItem('settings_accent_color') || defaultColor;
    const savedBackground = JSON.parse(localStorage.getItem('settings_background')) || {
        type: 'color',
        color: '#ffffff',
        image: '',
        darkness: 1,
        gradientColors: [{ color: '#ffffff', type: 'linear' }],
        css: '#ffffff',
    };

    const [accentColor, setAccentColor] = useState(savedColor);
    const [backgroundSettings, setBackgroundSettings] = useState(savedBackground);

    useEffect(() => {
        const savedColor = localStorage.getItem('settings_accent_color');
        if (savedColor) {
            setAccentColor(savedColor);
        }

        const savedBackground = JSON.parse(localStorage.getItem('settings_background'));
        if (savedBackground) {
            setBackgroundSettings(savedBackground);
        }
    }, []);

    return (
        <AccentColorContext.Provider value={{ accentColor, setAccentColor, backgroundSettings, setBackgroundSettings }}>
            {children}
        </AccentColorContext.Provider>
    );
};

export default AccentColorContext;
