import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';
import TrackList from './TrackList';
import MusicPlayer from './MusicPlayer';
import './App.css';

const App = () => {
    const [tracks, setTracks] = useState([]);
    const [currentTrack, setCurrentTrack] = useState(null);

    const handleSearch = async (query) => {
        try {
            const response = await axios.get(`https://api.deezer.com/search?q=${query}`);
            setTracks(response.data.data);
        } catch (error) {
            console.error('Error fetching data from Deezer API', error);
        }
    };

    const handlePlay = (trackUrl) => {
        setCurrentTrack(trackUrl);
    };

    return (
        <div className="App">
            <h1>Music Player</h1>
            <SearchBar onSearch={handleSearch} />
            <TrackList tracks={tracks} onPlay={handlePlay} />
            <MusicPlayer trackUrl={currentTrack} />
        </div>
    );
};

export default App;
