import React, { useState, useEffect, useContext } from 'react';
import AccentColorContext from './AccentColorContext';
import styles from './GeneralSettings.module.css';

const GeneralSettings = () => {
    const { accentColor, setAccentColor } = useContext(AccentColorContext);
    const [tempColor, setTempColor] = useState(accentColor);
    const [isColorChanged, setIsColorChanged] = useState(false);
    const [backgroundSettings, setBackgroundSettings] = useState({
        type: 'color',
        colors: ['#000000'],
        image: '',
        opacity: 1
    });

    useEffect(() => {
        setTempColor(accentColor);
    }, [accentColor]);

    useEffect(() => {
        // Загрузка сохраненных настроек фона
        const savedSettings = JSON.parse(localStorage.getItem('settings_background')) || {};
        if (savedSettings.css) {
            // Парсинг сохраненного значения css
            setBackgroundSettings(prev => ({
                ...prev,
                type: savedSettings.type || 'color',
                colors: savedSettings.colors || ['#000000'],
                image: savedSettings.image || '',
                opacity: savedSettings.opacity || 1
            }));
        }
    }, []);

    const handleColorChange = (event) => {
        setTempColor(event.target.value);
        setIsColorChanged(true);
        setAccentColor(event.target.value); // Изменение цвета в реальном времени
    };

    const handleSave = () => {
        localStorage.setItem('settings_accent_color', tempColor);

        const css = generateBackgroundCSS(backgroundSettings);
        localStorage.setItem('settings_background', JSON.stringify({
            css,
            type: backgroundSettings.type,
            colors: backgroundSettings.colors,
            image: backgroundSettings.image,
            opacity: backgroundSettings.opacity
        }));

        setIsColorChanged(false);
    };

    const handleRevert = () => {
        const savedColor = localStorage.getItem('settings_accent_color');
        if (savedColor) {
            setTempColor(savedColor);
            setIsColorChanged(false);
            setAccentColor(savedColor); // Восстановление сохраненного цвета
        }
    };

    const handleBackgroundTypeChange = (event) => {
        setBackgroundSettings(prev => ({ ...prev, type: event.target.value }));
    };

    const handleColorChangeInBackground = (index, color) => {
        setBackgroundSettings(prev => {
            const newColors = [...prev.colors];
            newColors[index] = color;
            return { ...prev, colors: newColors };
        });
        setIsColorChanged(true);
    };

    const handleAddColor = () => {
        if (backgroundSettings.colors.length < 6) {
            setBackgroundSettings(prev => ({
                ...prev,
                colors: [...prev.colors, '#000000']
            }));
        }
    };

    const handleRemoveColor = (index) => {
        setBackgroundSettings(prev => ({
            ...prev,
            colors: prev.colors.filter((_, i) => i !== index)
        }));
    };

    const handleImageChange = (event) => {
        setBackgroundSettings(prev => ({ ...prev, image: event.target.value }));
        setIsColorChanged(true);
    };

    const handleOpacityChange = (event) => {
        setBackgroundSettings(prev => ({ ...prev, opacity: event.target.value }));
        setIsColorChanged(true);
    };

    const generateBackgroundCSS = (settings) => {
        if (settings.type === 'color') {
            if (settings.colors.length === 1) {
                return `background-color: ${settings.colors[0]};`;
            } else {
                return `background: ${settings.colors.join(', ')};`;
            }
        } else if (settings.type === 'image') {
            return `background: url(${settings.image}) no-repeat center center; background-size: cover; opacity: ${settings.opacity};`;
        }
    };

    return (
        <div className={styles.settingsContainer}>
            {isColorChanged && (
                <div className={styles.unsavedChanges}>Есть несохраненные изменения</div>
            )}
            <h2>Общие и Персонализация</h2>
            <div className={styles.settingItem}>
                <label>Язык аккаунта:</label>
                <select disabled>
                    <option value="ru">Русский</option>
                    <option value="en">English</option>
                </select>
            </div>
            <div className={styles.settingItem}>
                <label>Тема:</label>
                <select disabled>
                    <option value="dark">Темная</option>
                    <option value="light">Светлая</option>
                </select>
            </div>
            <h2>Цвет и персонализация</h2>
            <div className={styles.settingItem}>
                <label>Цвет акцента:</label>
                <input
                    type="color"
                    value={tempColor}
                    onChange={handleColorChange}
                    className={styles.colorPicker}
                />
            </div>
            <div className={styles.settingItem}>
                <label>Цвет фона:</label>
                <select onChange={handleBackgroundTypeChange} value={backgroundSettings.type}>
                    <option value="color">Цвет</option>
                    <option value="image">Изображение</option>
                </select>
                {backgroundSettings.type === 'color' && (
                    <div>
                        {backgroundSettings.colors.map((color, index) => (
                            <div key={index} className={styles.colorPickerContainer}>
                                <input
                                    type="color"
                                    value={color}
                                    onChange={(e) => handleColorChangeInBackground(index, e.target.value)}
                                />
                                {backgroundSettings.colors.length > 1 && (
                                    <button
                                        type="button"
                                        onClick={() => handleRemoveColor(index)}
                                        className={styles.removeColorButton}
                                    >
                                        ×
                                    </button>
                                )}
                            </div>
                        ))}
                        {backgroundSettings.colors.length < 6 && (
                            <button
                                type="button"
                                onClick={handleAddColor}
                                className={styles.addColorButton}
                            >
                                Добавить цвет
                            </button>
                        )}
                        {backgroundSettings.colors.length > 1 && (
                            <div className={styles.gradientTypeContainer}>
                                <label>Тип градиента:</label>
                                <select>
                                    <option value="linear">Линейный</option>
                                    <option value="radial">Радиальный</option>
                                    {/* другие типы градиентов */}
                                </select>
                            </div>
                        )}
                    </div>
                )}
                {backgroundSettings.type === 'image' && (
                    <div>
                        <input
                            type="text"
                            value={backgroundSettings.image}
                            onChange={handleImageChange}
                            placeholder="URL изображения"
                        />
                        <label>Прозрачность:</label>
                        <input
                            type="range"
                            min="0.1"
                            max="1"
                            step="0.1"
                            value={backgroundSettings.opacity}
                            onChange={handleOpacityChange}
                        />
                        <span>{backgroundSettings.opacity}</span>
                    </div>
                )}
            </div>
            <div className={styles.buttonsContainer}>
                <button onClick={handleSave} className={styles.saveButton}>
                    Сохранить настройки
                </button>
                <button onClick={handleRevert} className={styles.revertButton}>
                    Отменить изменения
                </button>
            </div>
        </div>
    );
};

export default GeneralSettings;
