import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';
import TrackList from './TrackList';
import MusicPlayer from './MusicPlayer';
import './App.css';

const App = () => {
    const [tracks, setTracks] = useState([]);
    const [currentTrack, setCurrentTrack] = useState(null);

    const handleSearch = async (query) => {
        const accessToken = 'pwVbkuFjvcJOya5RnCnp';
        const userId = '52018093';

        try {
            const response = await axios.get('https://api.vk.com/method/audio.search', {
                params: {
                    q: query,
                    access_token: accessToken,
                    v: '5.131'
                }
            });
            setTracks(response.data.response.items.map(item => ({
                id: item.id,
                title: item.title,
                artist: item.artist,
                url: item.url
            })));
        } catch (error) {
            console.error('Error fetching data from VK API', error);
        }
    };

    const handlePlay = (trackUrl) => {
        setCurrentTrack(trackUrl);
    };

    return (
        <div className="App">
            <h1>Music Player</h1>
            <SearchBar onSearch={handleSearch} />
            <TrackList tracks={tracks} onPlay={handlePlay} />
            <MusicPlayer trackUrl={currentTrack} />
        </div>
    );
};

export default App;
